import { Route, Routes } from "react-router-dom";
import TagPage from "./TagPage";
import Home from "./Home.js";
import AddPage from "./AddPage.js";
import Layout from "./Layout";
import { useState, createContext } from "react";

//try manipulating background color of page by having a background color state and context? and changing it based on active
//property of navlink
//remove gray from css and make style document.body.style.backgroundColor = "green";

export const BackgroundContext = createContext(null);

function App() {
  const [backgroundColor, setBackgroundColor] = useState("green");
  document.body.style.backgroundColor = backgroundColor;

  return (
    <>
      <BackgroundContext.Provider value={[backgroundColor, setBackgroundColor]}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/data" element={<Layout />}>
            <Route path="add" element={<AddPage />} />
            <Route path="tag" element={<TagPage />} />
          </Route>
        </Routes>
      </BackgroundContext.Provider>
    </>
  );
}

export default App;
