import React, { useState } from "react";
import AddTags from "./AddTags";
import RandomData from "./RandomData";
import useBackgroundColor from "./useBackgroundColor";

const PORT_URL = "http://localhost:3000";

export default function TagPage() {
  useBackgroundColor("lightgreen");
  const [randomData, setRandomData] = useState(null); //will be an object with dataPoint, id and tags[]

  async function updateTags(tags) {
    const tagData = tags.filter((tag) => tag !== "");
    await fetch(`${PORT_URL}/tag/update`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
      body: JSON.stringify({ id: randomData.id, newTags: tagData }),
    });
  }

  return (
    <>
      <RandomData randomData={randomData} setRandomData={setRandomData} />

      {randomData && <AddTags updateTags={updateTags} />}
    </>
  );
}
//only show the addTags page if Data is available from the randomData call
