import React, { useState } from "react";
import AddTags from "./AddTags";
import RandomData from "./RandomData";
import useBackgroundColor from "./useBackgroundColor";
import { putTags, putRecentTag } from "./apiCalls";
import { Outlet } from "react-router-dom";

export default function TagPage() {
  useBackgroundColor("lightgreen");
  const [randomData, setRandomData] = useState(null); //will be an object with dataPoint, id and tags[]
  const [chosenTagFilter, setChosenTagFilter] = useState();
  const [updatedCount, setUpdatedCount] = useState(0); //using this to track when an update happens to trigger useEffect

  async function updateTags(tags) {
    const tagData = tags.filter((tag) => tag !== "");

    await putTags(randomData, tagData);
    await putRecentTag(chosenTagFilter);

    setRandomData(null);
    setUpdatedCount((prevUpdatedCount) => prevUpdatedCount + 1);
  }

  return (
    <>
      <RandomData
        randomData={randomData}
        setRandomData={setRandomData}
        chosenTagFilter={chosenTagFilter}
        setChosenTagFilter={setChosenTagFilter}
        updatedCount={updatedCount}
      />
      <Outlet />
      {randomData && (
        <AddTags updateTags={updateTags} randomData={randomData} />
      )}
    </>
  );
}
