import React, { useState, useEffect } from "react";

//move fetches to api

const PORT_URL = "http://localhost:3000";

export default function RandomData({
  randomData,
  setRandomData,
  chosenTagFilter,
  setChosenTagFilter,
  updatedCount,
}) {
  const [filterTags, setFilterTags] = useState([]);

  async function getRandomData() {
    const res = await fetch(`${PORT_URL}/data/random?tag=${chosenTagFilter}`, {
      method: "GET",
    });

    if (res.status === 404) {
      window.alert("No datapoint found with that filter");
      setRandomData(null);
      return;
    }

    const data = await res.json();
    setRandomData(data);
  }

  async function getRecentTag() {
    const res = await fetch(`${PORT_URL}/tag/recent`, {
      method: "GET",
    });
    const recentTag = await res.json();
    setChosenTagFilter(recentTag.tag);
    return recentTag;
  }

  //make custom hook useGetTags()
  useEffect(() => {
    async function getAllTags() {
      const res = await fetch(`${PORT_URL}/tag`, {
        method: "GET",
      });
      const tags = await res.json();
      tags.unshift(" ", "RANDOM DATA");
      setFilterTags(tags);
    }
    getAllTags();
    getRecentTag();
  }, [updatedCount]); //might need to change this dependency

  const displayFilterTags = filterTags.map((tag) => (
    <option key={Math.random()}>{tag}</option>
  ));

  const displayExistingTagsOnData = randomData
    ? randomData.tags.map((tag, index) => (
        <label key={index} className="existing-tag">
          {index === randomData.tags.length - 1 ? `${tag} ` : `${tag}, `}
        </label>
      ))
    : "";

  return (
    <>
      <div className="options-container">
        <label>Filter By Tag:</label>
        <select
          name="existing-tags"
          className="select-tag"
          value={chosenTagFilter}
          onChange={(e) => setChosenTagFilter(e.target.value)}
        >
          {displayFilterTags}
        </select>
        <button onClick={getRandomData}>Get Random Data</button>
      </div>
      <div className="data-container">
        <textarea
          readOnly={true}
          className="data-textarea"
          value={randomData ? randomData.dataPoint : ""}
        ></textarea>
      </div>
      <div className="existing-tags-container">
        <label>Existing Tags:</label>
        <ul>{displayExistingTagsOnData}</ul>
      </div>
    </>
  );
}

